import React from "react";
import { useParams } from 'react-router-dom'
import CommentSection from '../components/CommentSection'


export const ArticleDetail = ({ single }) => {
  let { id } = useParams();
  function like(){
    favCount++;
    favorite.innerHTML=` ${favCount}`
}
  return (
    <>
      <div className="detail">
        <div className="card" >
          {console.log(single)}
          <h1>{single.name}</h1>
          <p>{single.year}</p>
          <img src={`/image/img${(single.id) + 1}.jpg`} alt="hi" />
        </div>
        <div className="body">
          <p>{single.body}</p>
          <i class="fa fa-heart favorite" onClick={() => like()} id="favorite" aria-hidden="true">0</i>
          <hr class="my-2" />
          <i class="fa fa-comment" aria-hidden="true"></i>
          <div class="card-footer comments">
          </div>
        </div>
      </div>
    </>
  );
};
