import React, { useState } from 'react'
import { ArticleDetail } from "./ArticleDetail";
import { Link, Route } from 'react-router-dom'
import { nikeAir } from "../data";


function Blog({data}) {
    const [state, setState] = useState({
        post: {}
    })


    const fetchSingle = async (id)=>{
        let res = nikeAir[id]
        let data = await res;
       // console.log(data)
        setState({
            post:data
        })
    }

    return (
        <div className="blog">
            {data.map((item, i) => {
                return (
                    <div className="blogItem">
                    <Link className="link" onClick={()=>fetchSingle(item.id)}   key={i} to={`/blog/${item.name}`} style={{ 'display': 'block' }}>{item.name}<img className="pin" src={`/image/img${(item.id)+1}.jpg`} alt="hi"></img></Link>
                    </div>
                )
            })}
            <div>

            </div>


        </div>
    )
}

export default Blog
