import React from "react";
import { useParams } from 'react-router-dom'
import CommentSection from '../components/CommentSection'
import { Container, Row,Col } from 'reactstrap'

export const ArticleDetail = ({ single }) => {

  let { id } = useParams();

  return (
    <>
 
      <div className="detail">

        <div className="card" >
          {console.log(single)}
          <h2>{single.name}</h2>
          <h3>{single.year}</h3>
          <img src={`/image/img${(single.id) + 1}.jpg`} alt="hi" />
        </div>

        <Col md={6} className="mid mb-1 mt-1">
        <Row className="row">
        <div className="body">
          <p>{single.body}</p>  
        </div>
        </Row>
        </Col>
      </div>

    </>
  );
};
