import React from "react";
import { useParams } from 'react-router-dom'
import CommentSection from '../components/CommentSection'


export const ArticleDetail = ({ single }) => {

  let { id } = useParams();

  return (
    <>
      <div className="detail">
        <div className="card" >
          {console.log(single)}
          <h2>{single.name}</h2>
          <p>{single.year}</p>
          <img src={`/image/img${(single.id) + 1}.jpg`} alt="hi" />
        </div>
        <div className="body">
          <p>{single.body}</p>
         
        </div>
      </div>
    </>
  );
};
