import React from "react";
import { useParams } from 'react-router-dom'
import CommentSection from '../components/CommentSection'
import { Row,Col } from 'reactstrap'

export const ArticleDetail = ({ single }) => {

  let { id } = useParams();

  return (
    <>
    <Container className="Container m-auto text-center"></Container>
    <Row>
      <div className="detail">
        <Col md={4} xs={12}>
        <div className="card" >
          {console.log(single)}
          <h2>{single.name}</h2>
          <h3>{single.year}</h3>
          <img src={`/image/img${(single.id) + 1}.jpg`} alt="hi" />
        </div>
        </Col>
        <Col md={4} xs={12}>
        <div className="body">
          <p>{single.body}</p>  
        </div>
        </Col>
      </div>
      </Row>
    </>
  );
};
