import React from "react";
import { useParams } from 'react-router-dom'


export const ArticleDetail = ({single}) => {
  let {id} = useParams();
  return (
    <>
      <div className="detail">
                  <div >
                    {console.log(single)}
                      <h1>{single.name}</h1>
                      <p>{single.year}</p>
                      <img src={`/image/img${(single.id)+1}.jpg`} alt="hi"/>
                    
                      <p>{single.body}</p>
                  </div>
      </div>
    </>
  );
};
