import React from "react";
import { useParams } from 'react-router-dom'
import { Container, Row, Col } from 'reactstrap'

export const ArticleDetail = ({ single }) => {

  let { id } = useParams();

  return (
    <>
      <Container className="container m-auto">
    <Row className="row">
      <Col className="detail">
        {console.log(single)}
        <img src={`/image/img${(single.id) + 1}.jpg`} alt="hi" />
        </Col>
      <Col className="detail">
        <p>{single.body}</p>
        </Col>
        </Row>
      </Container>

    </>
  );
};
