import React from "react";
import { useParams } from 'react-router-dom'
import { Container, Row, Col } from 'reactstrap'

export const ArticleDetail = ({ single }) => {

  let { id } = useParams();

  return (
    <>
      <Container className="container m-auto">
        <Row className="row">
          <Col className="detail">
            
            <img src={`/image/img${(single.id) + 1}.jpg`} className="detailImg" alt="hi" />
          </Col>
          <Col className="detail" id="right">
            <h3 className="detailTitle">{single.name} ({single.year})</h3>
            <p className="detailBody">{single.body}</p>
          </Col>
        </Row>
      </Container>

    </>
  );
};
