import React, { Component } from 'react'
import fetch from 'node-fetch'
import { Button, Input, Label, Container, Row, Col } from 'reactstrap'



export default class App extends Component {
  state = {
    current: {}
  }
  getWeather = (cityname) => {
    return new Promise((resolve, reject) => {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=${cityname}&appid=886705b4c1182eb1c69f28eb8c520e20`)
        .then(data => data.json())
        .then(res => this.setState(res));

    })


  }

  render() {


    return (

      <div>
        <Container>
          <Row>
            <Col>
              <Label for="cityname">City Name</Label>
              <Input type="text" id="cityname"></Input>
              <Button onClick={() => this.getWeather(document.getElementById('cityname').value)}>Get Current Weather</Button></Col>
              <Col>
              <h1>Current Weather Situation</h1>
              <h3>Temprature: {this.state} </h3>
              <h3>Weather Condition: </h3>
              <h3>Weather Condition: </h3>

              </Col>
          </Row>

        </Container>
      </div>
    )
  }
}
