const DiceGame = () => {
    let dice1 = document.querySelectorAll('dice1');
    const dice1Image =()=>{

       let num1 = Math.floor(Math.random() * 6) + 1;
        
    
        if(num1 === 1){
            dice1.src = "Alea_1.png"
         }
         else if(num1===2){
            dice1.src = "Alea_2.png"
        
        }else if(num1===3){
            dice1.src = "Alea_3.png"
        
        }else if(num1===4){
            dice1.src = "Alea_4.png"
        
        }else if(num1===5){
            dice1.src = "Alea_5.png"
        }
        else{
            dice1.src = "Alea_6.png"
        }
    }

    return ( 

        <div className="container">
            <h1>Dice Game</h1>
            <div className="dice">
            <h4>Player 1</h4>
            <a href=""></a>
            <img src="img/Alea_1.png" alt="dice" id="dice1" onClick={dice1Image()}></img>
            <h4>Player 2</h4>
            <img src="img/Alea_1.png" alt="dice" id="dice2"></img>
            </div>
            
        </div>
     
            

     );
}
 
export default DiceGame;
