import { useState } from "react";
import Modal from 'react-modal'
import { Container,Row,Col } from 'reactstrap';
import StarRatings from './react-star-ratings';
 
class Foo extends Component {
    changeRating( newRating, name ) {
      this.setState({
        rating: newRating
      });
    }
 
    render() {
      // rating = 2;
      return (
        <StarRatings
          rating={this.state.rating}
          starRatedColor="blue"
          changeRating={this.changeRating}
          numberOfStars={6}
          name='rating'
        />
      );
    }
}
 
 
class Bar extends Component {
  render() {
    // aggregateRating = 2.35;
    return (
      <StarRatings
        rating={2.403}
        starDimension="40px"
        starSpacing="15px"
      />
    );
  }
}



const DiceGame = () => {
    const [result, setResult] = useState('');
    const [countUser, setCountUser] = useState(0)
    const [countComputer, setCountComputer] = useState(0)
    const [modalIsOpen, setModalIsOpen] = useState(false)
    const [winner, setWinner] = useState('')


    
    const rollDice = (e) => {



    
        //firstdice
        let randomNumb = Math.floor(Math.random() * 6) + 1;
        e.target.setAttribute('src', `img/Alea_${randomNumb}.png`)
        e.target.setAttribute('alt', 'dice')
        //seconddice
        let computerdice = e.target.parentElement.nextSibling.children[1]
        let randomNumb2 = Math.floor(Math.random() * 6) + 1;
        computerdice.setAttribute('src', `img/Alea_${randomNumb2}.png`)
        computerdice.setAttribute('alt', 'dice')
        //whowins

        if (randomNumb < randomNumb2) {
            setResult("Computer Wins")
            setCountComputer(countComputer + 1)
                if (countComputer == 5) {
                    console.log("computer wins")
                    setWinner("Computer won")
                    setTimeout(()=>{setModalIsOpen(true)}, 500)               
                };
        } else if (randomNumb > randomNumb2) {
            setResult("You Win")
            setCountUser(countUser + 1)
            if (countUser == 5) {
                console.log("you win")
                setWinner("You won")
                setTimeout(()=>{setModalIsOpen(true)}, 500)
            };
                
        } else {
            setResult("Equal")
        }        
    }

    return (
        <div  className="dicegame">
    <Container fluid>
        <Row fluid>
            <Col md={8} className="left">
                    <h1>DICE GAME</h1>
                    <h5>Click on your Dice to roll.</h5>
                    <div className='dicespan'>
                        <span className="dice">
                            <h4>You</h4>
                            <img src="img/Alea_1.png" alt="dice" onClick={rollDice}></img>
                        </span>
                        <span className="dice">
                            <h4>Computer</h4>
                            <img src="img/Alea_1.png" alt="dice" id="computerdice"></img>
                        </span>
                    </div>
            </Col>
            <Col md={4} className="score">
                    <h1>Score</h1>
                    <h5>You have to win 6 times!</h5>
                    <div className="result">
                    <h4 id="res">{result}</h4>
                    <h5>Computer: {countComputer}</h5>
                    <h5>You: {countUser}</h5>
                    <button onClick={()=>{setCountComputer(0); setCountUser(0); setModalIsOpen(false)}}>Restart</button>
                    </div>
            </Col>
            <Modal isOpen={modalIsOpen} onRequestClose={() => {setModalIsOpen(false);setCountComputer(0); setCountUser(0);}}>
                <h1>End of Game</h1>
                <h3>Final Score</h3>
                <p>Computer: {countComputer}</p>
                <p>You: {countUser}</p>
                <h2>{winner}</h2>
                <button onClick={()=>{setCountComputer(0); setCountUser(0); setModalIsOpen(false)}}>Restart</button>
            </Modal>
            </Row>
    </Container>
        </div>


        


    );
}

export default DiceGame;
