import { useState } from "react";

const DiceGame = () => {
    const [result, setResult] = useState('');
    const [computerCount, setComputerCount] = useState('')
    const [userCount, setUserCount] = useState('')
    const rollDice = (e) => {
        var counter1 = 0;
        var counter2 = 0;
        //firstdice
        let randomNumb = Math.floor(Math.random() * 6) + 1;
        e.target.setAttribute('src', `img/Alea_${randomNumb}.png`)
        e.target.setAttribute('alt', 'dice')
        //seconddice
        let computerdice = e.target.nextSibling.nextSibling
        let randomNumb2 = Math.floor(Math.random() * 6) + 1;
        computerdice.setAttribute('src', `img/Alea_${randomNumb2}.png`)
        computerdice.setAttribute('alt', 'dice')
        //whowins

        if (randomNumb < randomNumb2) {
            setResult("Computer Wins")
            counter1++;
            setComputerCount(counter1)
        } else if (randomNumb > randomNumb2) {
            setResult("You Win")
            counter2++;
            setUserCount(counter2)
        } else {
            setResult("Equal")
        }
        console.log(result)
    }

    return (
        <div className="dicegame">
            <h1>Dice Game</h1>
            <div className="dice">
                <h4>You</h4>
                <img src="img/Alea_1.png" alt="dice" onClick={rollDice}></img>
                <h4>Computer</h4>
                <img src="img/Alea_1.png" alt="dice" id="computerdice"></img>
            </div>
            <div className="result">
                <h2 id="res">{result}</h2>
                <h2>Score</h2>
                <p>Computer:{computerCount}</p>
                <p>You:{userCount}</p>
            </div>
        </div>

    );
}

export default DiceGame;
