import { useState } from "react";
import Modal from 'react-modal'

const DiceGame = () => {
    const [result, setResult] = useState('');
    const [countUser, setCountUser] = useState(0)
    const [countComputer, setCountComputer] = useState(0)
    const [modalIsOpen, setModalIsOpen] = useState(false)
    const [winner, setWinner] = useState('')

    const getWinner = () => {
        if (countUser == 6) {
            console.log("you win")
            setWinner("You won")
            setModalIsOpen(true)
        } else if (countComputer == 6) {
            console.log("computer wins")
            setWinner("Computer won")
            setModalIsOpen(true)
        }
    }
    
    const rollDice = (e) => {

        //firstdice
        let randomNumb = Math.floor(Math.random() * 6) + 1;
        e.target.setAttribute('src', `img/Alea_${randomNumb}.png`)
        e.target.setAttribute('alt', 'dice')
        //seconddice
        let computerdice = e.target.parentElement.nextSibling.children[1]
        console.log(computerdice)
        let randomNumb2 = Math.floor(Math.random() * 6) + 1;
        computerdice.setAttribute('src', `img/Alea_${randomNumb2}.png`)
        computerdice.setAttribute('alt', 'dice')
        //whowins

        if (randomNumb < randomNumb2) {
            setResult("Computer Wins")
            setCountComputer(countComputer + 1)
        } else if (randomNumb > randomNumb2) {
            setResult("You Win")
            setCountUser(countUser + 1)
        } else {
            setResult("Equal")
        }            
    }

    return (
        <div className="dicegame">
            <h1>Dice Game</h1>
            <h4>You have to win 6 times!</h4>
            <div>
                <span className="dice">
                    <h4>You</h4>
                    <img src="img/Alea_1.png" alt="dice" onClick={rollDice}></img>
                </span>
                <span className="dice">
                <h4>Computer</h4>
                <img src="img/Alea_1.png" alt="dice" id="computerdice"></img>
                </span>
            </div>
            <div className="result">
                <h2 id="res">{result}</h2>
                <h2>Score</h2>
                <p>Computer:{countComputer}</p>
                <p>You:{countUser}</p>
            </div>
            {countComputer==6 &             <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)}>
                <h1>End of Game</h1>
                <h3>Final Score</h3>
                <p>Computer:{countComputer}</p>
                <p>You:{countUser}</p>
                <h2>{winner}</h2>

            </Modal>}

        </div>

    );
}

export default DiceGame;
