const DiceGame = () => {
    let dice1 = document.getElementById('dice1');
    let dice2 = document.getElementById('dice2');

    function dice1Image(){

        num1 = Math.floor(Math.random() * 6) + 1;
        
    
        if(num1 === 1){
            dice1.src = "Alea_1.png"
         }
         else if(num1===2){
            dice1.src = "Alea_2.png"
        
        }else if(num1===3){
            dice1.src = "Alea_3.png"
        
        }else if(num1===4){
            dice1.src = "Alea_4.png"
        
        }else if(num1===5){
            dice1.src = "Alea_5.png"
        }
        else{
            dice1.src = "Alea_6.png"
        }
    }
    
    
    function dice2Image(){
    
        num2 = Math.floor(Math.random() * 6) + 1;
        
    
        if(num2 === 1){
            dice2.src = "Alea_1.png"
         }
         else if(num2===2){
            dice2.src = "Alea_2.png"
        
        }else if(num2===3){
            dice2.src = "Alea_3.png"
        
        }else if(num2===4){
            dice2.src = "Alea_4.png"
        
        }else if(num2===5){
            dice2.src = "Alea_5.png"
        }
        else{
            dice2.src = "Alea_6.png"
        }
    }
    ;

    return ( 

        <div className="container">
            <h1>Dice Game</h1>
            <button>Roll Dices</button>
            <div className="dice">
            <h4>Player 1</h4>
            <img src="img/Alea_1.png" alt="dice" id="dice1"></img>
            <h4>Player 2</h4>
            <img src="img/Alea_1.png" alt="dice" id="dice2"></img>
            </div>
        </div>
            

     );
}
 
export default DiceGame;
