import { useState } from "react";

const JumpGame = () => {
    var audio = new Audio('http://www.superluigibros.com/downloads/sounds/SNES/SMK/wav/jump.wav')
    var audio2 = new Audio('https://dight310.byu.edu/media/audio/FreeLoops.com/1/1/Blip%20Sound%20Fx-12783-Free-Loops.com.mp3')
    var audio3 = new Audio('http://www.mario-museum.net/sons/smb_gameover.wav')
    var character = document.getElementById("character")
    var block = document.getElementById("block")
    let timeLeft = 3
    const [countDown, setCountdown] = useState(timeLeft)
    const [score, setScore] = useState(0)

    const jump = () => {
        if (character.classList != "animate") {
            audio.play()
            character.classList.add("animate")
        }
        setTimeout(function () {
            character.classList.remove("animate")
        }, 500)
    }
    const startGame = () => {
        audio2.play();
        setInterval(function(){
            if(timeLeft <=0){
                clearInterval(timeLeft = 0)
            }else{
                timeLeft -=1
            }
            setCountdown(timeLeft)

        },1000)
        
        setTimeout(function () {
         (document.getElementById("block")).style.animation = " block 1s infinite";
        },4000)


        var checkDead = setInterval(function () {
            var characterTop = parseInt(window.getComputedStyle(document.getElementById("character")).getPropertyValue("top"));
            var blockLeft = parseInt(window.getComputedStyle(document.getElementById("block")).getPropertyValue("left"));
            if (blockLeft < 20 && blockLeft > 0 && characterTop >= 130) {
                (document.getElementById("block")).style.animation = "none";
                audio3.play();
            }else{
                
            }
        }, 10)

    }


    return (
        <div className="jumpgame">
            <div className="top">
                <h1>JUMP GAME</h1>
            <button onClick={()=>{startGame();}}>start</button>
            <h3><span onChange={audio2.play()}>{countDown}</span></h3>
            <h5>score</h5>
            </div>

            <div id="game"  onClick={()=>{jump();}}>
                <div id="character"></div>
                <div id="block"></div>
            </div>
        </div>
    );
}

export default JumpGame;
